class Solution {
    Long[][] dp;
    Integer[][] store;
    int MIN = -1*((int)1e8 + 5);
    long helper(int w, int h, int[][] prices){
        if(w == 0 || h == 0) return 0;

        if(dp[w][h] != null) return dp[w][h];
        // if(store[w][h] != null) return store[w][h];

        long max = (store[w][h] != null) ? store[w][h] : 0;
        // making vertical cut...
        for(int i = 1; i < w; i++){
            long a = helper(w - i, h, prices) + helper(i, h, prices);
            max = Math.max(max, a);
        }

        // making horizontal cut
        for(int i = 1; i < h; i++){
            long a = helper(w, h - i, prices) + helper(w, i, prices);
            max = Math.max(max, a);
        }

        return dp[w][h] = max;
        // System.out.println(" --> " + max);
        // return max;
