func sellingWood(m int, n int, prices [][]int) int64 {
    maxp, memo, dictp := make([][]int, m + 1), make([][]int, m + 1), make([][]int, m + 1)
    for i := 0; i < m + 1; i++ {
        maxp[i] = make([]int, n + 1)
        memo[i] = make([]int, n + 1)
        dictp[i] = make([]int, n + 1)
        for j := 0; j < n + 1; j++ {
            memo[i][j] = -1
        }
    }

    for _, p := range prices {
        dictp[p[0]][p[1]] = max(dictp[p[0]][p[1]], p[2])
    }

    get_maxp(m, n, maxp, dictp)

    return int64(dfs(m, n, maxp, memo))
}

func get_maxp(m int, n int, maxp [][]int, dictp [][]int) {
    for i := 1; i <= m; i++ {
        for j := 1; j <= n; j++ {
            maxp[i][j] = max(maxp[i - 1][j], maxp[i][j - 1])
            maxp[i][j] = max(maxp[i][j], dictp[i][j])
        }
    }
}

func dfs(m int, n int, maxp [][]int, memo [][]int) int {    
    if memo[m][n] != -1 {
        return memo[m][n]
    }

    res := maxp[m][n]

    for i := 1; i <= m / 2; i++ {
        res = max(res, dfs(i, n, maxp, memo) + dfs(m - i, n, maxp, memo))
    }

    for i := 1; i <= n / 2; i++ {
        res = max(res, dfs(m, i, maxp, memo) + dfs(m, n - i, maxp, memo))
    }

    memo[m][n] = res
    return res
}

func max(a int, b int) int {
    if a > b {
        return a
    }
    return b
