                for ii in range(1,i//2+1):
                    x = dp[ii][j]+dp[i-ii][j]
                    if x>dp[i][j]:
                        dp[i][j] = x 
                for jj in range(1,j//2+1):
                    x = dp[i][jj]+dp[i][j-jj]
                    if x>dp[i][j]:
                        dp[i][j] = x


        return dp[m][n]

