public class Solution 
{
    public long SellingWood(int m, int n, int[][] prices) 
    {
        var pricesDict = prices.ToDictionary(price => (price[0], price[1]), price => price[2]);
        Dictionary<(int, int), long> dp = new();

        return Work(m, n);

        long Work(int x, int y) 
        {
            if (x == 0 || y == 0) return 0;

            if (!dp.ContainsKey((x, y))) 
            {
                long ans = pricesDict.ContainsKey((x, y)) ? pricesDict[(x, y)] : 0;

                for (int i = 1; i <= x / 2; ++i) ans = Math.Max(ans, Work(i, y) + Work(x - i, y));
                for (int i = 1; i <= y / 2; ++i) ans = Math.Max(ans, Work(x, i) + Work(x, y - i));

                dp[(x, y)] = ans;
            }  

            return dp[(x, y)];
        }
    }
}
