class Solution {
public:
    static bool comp(vector<int> a, vector<int> b)
    {
        return a[2]>b[2];
    }

    vector<vector<int>> mem;
    vector<vector<int>> vis;

    long long rec(int m, int n)
    {
        if(m==0 || n==0 || mem[m][n]==0)
            return 0;
        if(vis[m][n])
            return mem[m][n];
        vis[m][n]=1;
        long long sum=mem[m][n];
        for(int i=1; i<m; i++)
            sum=max(sum, rec(i, n)+rec(m-i, n));
        for(int i=1; i<n; i++)
            sum=max(sum, rec(m, i)+rec(m, n-i));
        mem[m][n]=sum;
        return sum;
    }

    long long sellingWood(int m, int n, vector<vector<int>>& prices) {
        mem.assign(m+1, vector<int>(n+1, 0));
        vis.assign(m+1, vector<int>(n+1, 0));
        sort(prices.begin(), prices.end(), comp);
        for(int k=0; k<prices.size(); k++)
        {
            if(mem[prices[k][0]][prices[k][1]])
                continue;
            for(int i=prices[k][0]; i<=m; i++)
                for(int j=prices[k][1]; j<=n; j++)
                {
                    if(mem[i][j])
                        break;
                    mem[i][j]=prices[k][2];
                }
        }
        return rec(m, n);
    }
};
