            if(lk[h][w] > LLONG_MIN) return lk[h][w];
            lk[h][w] = init[h][w] > LLONG_MIN? init[h][w] : 0;            
            return lk[h][w];
        }

        // check cache
        if(lk[h][w] > LLONG_MIN) return lk[h][w];

        long long maxx = LLONG_MIN;
        if(init[h][w] > LLONG_MIN) maxx = init[h][w];
        // split left
        for(int i = 1; i < h ; i++){
            long long top = dfs(i, w , lk, init);
            long long bottom = dfs(h-i, w, lk, init);
            maxx = max(maxx, top+bottom);
        }
        // split top
        for(int i = 1; i < w ; i++){
            long long left = dfs(h, i, lk, init);
            long long right = dfs(h, w-i, lk, init);
            maxx = max(maxx, left+right);
        }
        lk[h][w] = maxx;

        return lk[h][w];
    }

    long long sellingWood(int m, int n, vector<vector<int>>& prices) {

        vector<vector<long long>> lk(m+1, vector<long long>(n+1 , LLONG_MIN));
        vector<vector<long long>> init(m+1, vector<long long>(n+1 , LLONG_MIN));
        // load init
        for(auto price: prices){
            int h = price[0];
            int w = price[1];
            int p = price[2];
            init[h][w] = p;
        }

        return dfs(m, n , lk, init);

    }
};
