class Solution {
public:
    long long sellingWood(int m, int n, vector<vector<int>>& prices) {

        long long i, j;

        vector<vector<long long>>dp(m + 1, vector<long long>(n + 1, -1));

        vector<vector<long long>>p(m + 1, vector<long long>(n + 1, LLONG_MIN));


        for (i = 0; i < prices.size(); i++) {
            p[prices[i][0]][prices[i][1]] = prices[i][2];
        }


        return solve(m, n, dp, p);
    }

    long long solve(long long m, long n, vector<vector<long long>>& dp, vector<vector<long long>>& p) {

        long long ans = 0;

        if (m == 1 && n == 1) {
		// can't cut further
            return max(0LL, p[m][n]);
        }

        if (dp[m][n] != -1) {
            return dp[m][n];
        }

        ans = p[m][n];

        long long i, j, k;

		// cut horizontally
        for (i = 1; i < m; i++) {
            ans = max(ans, solve(i, n, dp, p) + solve(m - i, n, dp, p));
        }

		// cut vertically
        for (i = 1; i < n; i++) {
            ans = max(ans, solve(m, i, dp, p) + solve(m, n - i, dp, p));
        }

        return dp[m][n] = ans;
    }
};
