        r = p[h][w]
    }

    for i := 1; i < h; i++ {
        x := dp(i, w, memo, p) + dp(h - i, w, memo, p)
        if x > r {
            r = x
        }        
    }

    for j := 1; j < w; j++ {
        x := dp(h, j, memo, p) + dp(h, w - j, memo, p)
        if x > r {
