class Solution {
public:
    long long sellingWood(int m, int n, vector<vector<int>>& prices) {
        unordered_map<string, int> hm;
        for (auto& price: prices)
        {
            hm[to_string(price[0])+'_'+to_string(price[1])] = price[2];
        }
        vector<vector<long long>> memo(m+1, vector<long long>(n+1, -1));
        return helper(m, n, hm, memo);
    }
private:
    long long helper(int m, int n, unordered_map<string, int>& hm, vector<vector<long long>>& memo)
    {
        if (m <= 0 || n <= 0) return 0;
        if (memo[m][n] != -1) return memo[m][n];
        string shape = to_string(m)+'_'+to_string(n);
        long long res = 0;
        if (hm.find(shape) != hm.end()) res = static_cast<long long>(hm[shape]);
        // horizontal cut
        for (int i=1; i<m; i++)
        {
            res = max(res, helper(i, n, hm, memo)+helper(m-i, n, hm, memo));
        }
        // vertical cut
        for (int i=1; i<n; i++)
        {
            res = max(res, helper(m, i, hm, memo)+helper(m, n-i, hm, memo));
        }
        return memo[m][n] = res;
    }
};
