class Solution {
 public:
  long long sellingWood(const int m, const int n, const vector<vector<int>>& prices) {
    int profit[m + 1][n + 1];
    memset(profit, 0, sizeof(profit));
    for (const vector<int> &price : prices) {
      profit[price[0]][price[1]] = price[2];
    }

    long long dp[m + 1][n + 1];
    memset(dp, 0, sizeof(dp));
    for (int rows = 1; rows < m + 1; ++rows) {
      for (int cols = 1; cols < n + 1; ++cols) {
        dp[rows][cols] = profit[rows][cols];
        for (int top = 1; top < rows; ++top) {
          dp[rows][cols] = max(dp[rows][cols], dp[top][cols] + dp[rows - top][cols]);
        }
        for (int left = 1; left < cols; ++left) {
          dp[rows][cols] = max(dp[rows][cols], dp[rows][left] + dp[rows][cols - left]);
        }
      }
    }
    return dp[m][n];
  }
};
