    long long board[201][201];

public:
    long long sellingWood(int m, int n, vector<vector<int>>& prices) 
    {
        for (auto &p : prices)
        {
            int r = p[0];
            int c = p[1];
            int cost = p[2];
            board[r][c] = cost;
        }
        for (int i = 1; i <= m; i++)
        {
            for (int j = 1; j <= n; j++) 
            {
                // 2 Options: 

                // Split the i * j board at row i - k to give 2 boards k * j, (i - k) * j
                for (int k = 1; k < i; k++)
                    board[i][j] = max(board[i][j], board[k][j] + board[i - k][j]);

                // Split the i * j board at col j - k to give 2 boards i * k, i * (j - k)
                for (int k = 1; k < j; k++)
                    board[i][j] = max(board[i][j], board[i][k] + board[i][j - k]);
            }
        }
        return board[m][n];
    }
};
