class Solution:
    def sellingWood(self, m: int, n: int, prices: List[List[int]]) -> int:
        pDict = {}
        for h,w,p in prices:
            pDict[(h,w)] = p

        @lru_cache(None)
        def dp(H,W):
            ans = 0
            if (H,W) in pDict:
                ans = pDict[(H,W)]
            for i in range(1,H):
                ans = max(ans, dp(H-i,W) + dp(i,W))
            for j in range(1,W):
                ans = max(ans, dp(H,W-j)+dp(H,j))
            return ans
        return dp(m,n)
