/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:

    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        vector<int> val;
        int len=0;
        while(head!=NULL)
        {
            val.push_back(head->val);
            head=head->next;
            len++;
        }
        int left=m*n-len;
        while(left--)
        {
            val.push_back(-1);
        }
        vector<vector<int>> mat(m,vector<int>(n,0));
        int z=val.size();

        int uprow=0,downrow=m-1;
        int leftcol=0,rightcol=n-1;
        int c=0;
        while(c!=z)
        {

            for(int i=leftcol;i<=rightcol;i++)
            {
                if(c==z)
                {
                    break;
                }
                mat[uprow][i]=val[c++];
            }
            uprow++;
            for(int j=uprow;j<=downrow;j++)
            {
                if(c==z)
                {
                    break;
                }
                mat[j][rightcol]=val[c++];
            }
            rightcol--;
            for(int i=rightcol;i>=leftcol;i--)
            {
                if(c==z)
                {
                    break;
                }
                mat[downrow][i]=val[c++];
            }
            downrow--;
            for(int i=downrow;i>=uprow;i--)
            {
                if(c==z)
                {
                    break;
                }
                mat[i][leftcol]=val[c++];
            }
            leftcol++;
            if(c==z)
                {
                    break;
                }
        }
        return mat;
    }
};
