class Solution {
public:
    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        vector<vector<int>> mat(m, vector<int>(n, -1));
        int i=0, j=-1, num_x=m, num_y=n, counter;

        while(head){
            //left
            counter=num_y;
            while(head && counter--){
                mat[i][++j]=head->val;
                head = head->next;
            }
            num_x--;

            //down
            counter=num_x;
            while(head && counter--){
                mat[++i][j]=head->val;
                head = head->next;
            }
            num_y--;

            //right
            counter=num_y;
            while(head && counter--){
                mat[i][--j]=head->val;
                head = head->next;
            }
            num_x--;

            //up
            counter=num_x;
            while(head && counter--){
                mat[--i][j]=head->val;
                head = head->next;
            }
            num_y--;
        }
        return mat;
    }
};
