        int bottom = n - 1;

        int left = 0;

        int right = m - 1;

        while(top <= bottom && left <= right)
        {
            // fill the top row

            if(top <= bottom)
            {
                for(int i = left; i <= right && curr; i++)
                {
                    res[top][i] = curr -> val;

                    curr = curr -> next;
                }

                top++;
            }

            // fill the right col

            if(left <= right)
            {
                for(int i = top; i <= bottom && curr; i++)
                {
                    res[i][right] = curr -> val;

                    curr = curr -> next;
                }

                right--;
            }

            // fill the bottom row

            if(top <= bottom)
            {
                for(int i = right; i >= left && curr; i--)
                {
                    res[bottom][i] = curr -> val;

                    curr = curr -> next;
                }

                bottom--;
            }

            // fill the left col

            if(left <= right)
            {
                for(int i = bottom; i >= top && curr; i--)
                {
                    res[i][left] = curr -> val;

                    curr = curr -> next;
                }

                left++;
            }
        }

        return res;
    }
};
