                    ans[rowEnd][j] = temp.val;
                    temp = temp.next;
                }
            }

            // Left -->
            for(int i = rowEnd - 1; i >= rowStart + 1; i--) {
                if(colStart == colEnd) break; // V.V.I (base condition)
                if(temp == null) {
                    ans[i][colStart] = -1;
                } else {
                    ans[i][colStart] = temp.val;
                    temp = temp.next;
                }
            }

            rowStart++;
            rowEnd--;
            colStart++;
            colEnd--;
        }

        return ans;
    }
}
