                d++;
            } else if (d == 1 && x == border[d] - r) {
                d++;
            } else if (d == 2 && y == border[d] + r) {
                d++;
            } else if (d == 3 && x == border[d] + r) {
                d = 0;
                r++;
            }

            x = x + dx[d];
            y = y + dy[d];

            c++;
        }

        return res;

    }
}
