// Time complexity = O(n*m)
// Space complexity = O(n*m) or O(1) if we ignore the results array

class Solution {
    public int[][] spiralMatrix(int m, int n, ListNode head) {
        int[][] result = new int[m][n];
        ListNode curr = head;
        int top = 0, right = n-1, down = m-1, left = 0;

        while(top <= down && left <= right) {
            //left to right
            for(int i = left; i <= right; i++) {
                if(curr == null) {
                    result[top][i] = -1;
                }
                else {
                    result[top][i] = curr.val;
                    curr = curr.next;
                }
            }

            //right to bottom
            for(int i = top + 1; i <= down; i++) {
                if(curr == null) {
                    result[i][right] = -1;
                }
                else {
                    result[i][right] = curr.val;
                    curr = curr.next;
                }
            }

            //bottom to left
            //We check first if top < down, because if they are equal then this will 
            //overwrite the "left to right" step above
            if(top < down) {
                for(int i = right - 1; i >= left; i--) {
                    if(curr == null) {
                        result[down][i] = -1;
                    }
                    else {
                        result[down][i] = curr.val;
                        curr = curr.next;
                    }
                }
            }

            //left to top
            //We check first if left < right, because if they are equal then this will
            //overwrite the "right to bottom" step above
            if(left < right) {
                for(int i = down - 1; i > top; i--) {
                    if(curr == null) {
                        result[i][left] = -1;
                    }
                    else {
                        result[i][left] = curr.val;
                        curr = curr.next;
                    }
                }
            }

            top++; left++; right--; down--;
        }

        return result;
    }
}
