  vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        vector<int> ll;

        while(head != NULL){
            ll.push_back(head->val);
            head = head->next;
        }

        vector<vector<int>> arr(m, vector<int>(n, -1));

        int r1 = 0, r2 = m-1;
        int c1 = 0, c2 = n-1;

        int i = 0;

        while(r1 <= r2 and c1 <= c2){
            if(i == ll.size()) break;
            //left to right
            for(int c=c1; c<=c2; c++) {
                if(i == ll.size()) break;
                arr[r1][c] = ll[i++];
            }

            //move down
            for(int r=r1+1; r<=r2; r++) {
                if(i == ll.size()) break;
                arr[r][c2] = ll[i++];
            }

            //right to left and move up
            if(r1 < r2 and c1 < c2){
                if(i == ll.size()) break;
                //right to left

                for(int c=c2-1; c>c1; c--) {
                    if(i == ll.size()) break;
                    arr[r2][c] = ll[i++];
                }

                //up
                for(int r=r2; r>r1; r--) {
                    if(i == ll.size()) break;
                    arr[r][c1] = ll[i++];
                }
            }
            r1++;
            r2--;
            c1++;
            c2--;
        }
        return arr;
    }
};
