class Solution {
public:
    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        vector<vector<int>> ans(m, vector<int>(n,-1));
        int top = 0,down = m-1,left =0,right = n-1; 
        int dir = 0;    
        while(top<=down && left<=right){
            if(dir==0){
                for(int i = left;i<=right;i++){
                    if(head->next!=NULL){
                    ans[top][i] = head->val;
                    head = head->next;
                    }else{
                        ans[top][i] = head->val;
                        return ans;
                        exit(1);
                    }
                }
                top++;
            }

          else if(dir==1){
              for(int i=top;i<=down;i++){
                  if(head->next!=NULL){
                      ans[i][right] = head->val;
                      head = head->next;
                  }else{
                      ans[i][right] = head->val;
                      return ans;
                      exit(1);
                  }
              }
              right--;
          }
        else if(dir == 2){
            for(int i=right;i>=left;i--){
                if(head->next!=NULL){
                    ans[down][i] = head->val;
                    head = head->next;
                }else{
                    ans[down][i] = head->val;
                    return ans;
                    exit(1);
                }
            }
            down--;
        }
        else if(dir == 3){
            for(int i=down;i>=top;i--){
                if(head->next!=NULL){
                    ans[i][left] = head->val;
                    head = head->next;
                }else{
                    ans[i][left] = head->val;
                    return ans;
                    exit(1);
                }
            }
            left++;
        }
            dir = (dir + 1)%4;

        }
        return ans;
    }
};
