            for(int i=down;i>=top;i--){
                if(head->next!=NULL){
                    ans[i][left] = head->val;
                    head = head->next;
                }else{
                    ans[i][left] = head->val;
                    return ans;
                    exit(1);
                }
            }
            left++;
        }
            dir = (dir + 1)%4;

        }
        return ans;
    }
};
