class Solution {
public:
    vector<vector<int>> spiralMatrix(int n, int m, ListNode* head) {
        vector<vector<int>> matrix(n,vector<int> (m,-1));

        int left =0, right = m-1, top =0, bottom = n-1;

        int i;
        ListNode *temp = head;

        while(left <= right and top <= bottom and temp!=NULL) {

            // left to right in the top row
            for(i = left; i <= right; i++) {
                if(temp) {matrix[top][i] = temp->val;
                temp = temp ->next;}
            }
            // now that top row is done,decrement the top pointer
            top++;

            if(left > right || top > bottom || temp == NULL) break;

            //top to bottom in the rightmost column
            for(i = top; i <= bottom; i++) {

                 if(temp) { matrix[i][right] = temp->val;
                temp = temp ->next;}
            }
            right--;

            if(left > right || top > bottom || temp == NULL) break;

            //right to left in the bottom row
            for(i = right; i >= left; i--) {

                if(temp) {matrix[bottom][i] = temp->val;
                temp = temp ->next;}
            }
            bottom--;

            if(left > right || top > bottom || temp == NULL) break;

            //bottom to top in the left most column
            for(i=bottom; i >= top; i--) {

                if(temp) {matrix[i][left] = temp->val;
                temp = temp->next;}

            }
            left++;

            if(left > right || top > bottom || temp == NULL) break;
        }

        return matrix;

    }
};
