            res[i][j] = head.val;
            head = head.next;

            if (!isOK(i + di, j + dj)) (di, dj) = (dj, -di);

            bool isOK(int x, int y) => x >= 0 && x < m && y >= 0 && y < n && res[x][y] == -1; 
        }
        return res;
    }
