        vector<vector<int>> mat(m,vector<int>(n));

        int left=0 , right=n-1; // column pointers
        int top=0 , bottom=m-1; // row pointers
        int dir = 0;
        while(left<=right && top<=bottom) {
            if(dir == 0) {
                for(int i=left ; i<=right ; i++) {
                    mat[top][i] = (head) ? head->val : -1;
                    if(head) head = head->next;
                }
                top++;
            }

            if(dir == 1) {
                for(int i=top ; i<=bottom ; i++) {
                    mat[i][right] = (head) ? head->val : -1;
                    if(head) head = head->next;
                }
                right--;
            }

            if(dir == 2) {
                for(int i=right ; i>=left ; i--) {
                    mat[bottom][i] = (head) ? head->val : -1;
                    if(head) head = head->next;
                }
                bottom--;
            }

            if(dir == 3) {
                for(int i=bottom ; i>=top ; i--) {
                    mat[i][left] = (head) ? head->val : -1;
                    if(head) head = head->next;
                }
                left++;
            }

            dir = (dir+1)%4;
        }
        return mat;
    }
