class Solution {
    public int[][] spiralMatrix(int m, int n, ListNode head) {

        int[][] mat = new int[m][n];
        int cnt = 0;

        // boundaies
        // Left and right
        int lb = 0, rb = n-1;
        // ceil and floor
        int c = 0, f = m-1;

        while(cnt < (m*n)){

            // Fill L to R
            for(int i = lb; i <= rb ; i++){

                cnt++;
                if(head != null){   
                    mat[c][i] = head.val;
                    head = head.next;
                }else{
                    mat[c][i] = -1;
                }
            }

            // Fill C to F
            for(int j = c+1; j <= f ; j++){

                cnt++;
                if(head != null){   
                    mat[j][rb] = head.val;
                    head = head.next;
                }else{
                    mat[j][rb] = -1;
                }
            }

            // Fill R to L
            if(f != c){
                for(int i = rb-1; i >= lb ; i--){

                    cnt++;
                    if(head != null){   
                        mat[f][i] = head.val;
                        head = head.next;
                    }else{
                        mat[f][i] = -1;
                    }
                }
            }

            // Fill F to C
            if(lb != rb){
                for(int j = f-1; j > c ; j--){

                    cnt++;
                    if(head != null){   
                        mat[j][lb] = head.val;
                        head = head.next;
                    }else{
                        mat[j][lb] = -1;
                    }
                }
            }

            // Increment
            lb++;
            rb--;
            c++;
            f--;
        }

        return mat;
    }
}
