/**
 * Example:
 * var li = ListNode(5)
 * var v = li.`val`
 * Definition for singly-linked list.
 * class ListNode(var `val`: Int) {
 *     var next: ListNode? = null
 * }
 */
class Solution {
    fun spiralMatrix(m: Int, n: Int, head: ListNode?): Array<IntArray> {
        var matrix = Array<IntArray>(m){IntArray(n){-1}}
        head ?: return matrix
        var R = arrayOf(0,1,0,-1)
        var C = arrayOf(1,0,-1,0)
        var (row,col,dr,dc,i) = listOf(0,0,0,0,1)
        var size = m*n
        var curr = head 
        while(i++ <= size){
            matrix[row][col] = curr!!.`val`
            curr = curr?.next
            if(curr == null) break
            var r = Math.floorMod(row+R[dr],m)
            var c = Math.floorMod(col+C[dc],n)
            if(matrix[r][c] != -1){
                dr = (dr+1) % 4
                dc = (dc+1) % 4
            }
            row += R[dr]
            col += C[dc]
        }
        return matrix
    }
}
