/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        vector<vector<int>> v(m, vector<int>(n, -1));
        int mend = m, nend = n, cnt = 0, size = m*n;
        int i = 1, j = 1, mst = 1, nst = 1;
        while(cnt < size && head){
            i = nst;
            j = mst;
            while(i <= nend){
                v[j-1][i-1] = head->val;
                head = head->next;
                if(!head) break;
                ++i;
                ++cnt;
            }
            --i;
            ++j;
            if(!head) break;
            while(j <= mend){
                v[j-1][i-1] = head->val;
                head = head->next;
                if(!head) break;
                ++j;
                ++cnt;
            }
            --j;
            --i;
            if(!head) break;
            while(i >= nst){
                v[j-1][i-1] = head->val;
                head = head->next;
                if(!head) break;
                --i;
                ++cnt;
            }
            ++i;
            --j;
            if(!head) break;
            while(j >= mst+1){
                v[j-1][i-1] = head->val;
                head = head->next;
                if(!head) break;
                --j;
                ++cnt;
            }
            ++j;
            if(!head) break;
            ++nst;
            --nend;
            ++mst;
            --mend;
        }
        return v;
    }
};
