class Solution {
public:
    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        vector<vector<int>> spiral(m,vector<int>(n,-1));
        ListNode* curr = head;
        int rowStart = 0, colStart = 0, rowEnd = m-1, colEnd = n-1;

        while(curr)
        {   //fill first row of current rectangle
            for(int j=colStart; j<=colEnd; j++)
            {
                if(curr)
                {
                spiral[rowStart][j] = curr->val;
                curr = curr->next;
                }
            }
            //fill last col of current rectangle
            for(int i=rowStart+1; i<=rowEnd; i++)
            {
                if(curr)
                {
                spiral[i][colEnd] = curr->val;
                curr = curr->next;
                }    
            }
            //fill last row of current rectangle
            for(int j=colEnd-1; j>=colStart; j--)
            {
                if(curr)
                {
                spiral[rowEnd][j] = curr->val;
                curr = curr->next;
                }
            }
            //fill first col of current rectangle
            for(int i=rowEnd-1; i>rowStart; i--)
            {
                if(curr)
                {
                spiral[i][colStart] = curr->val;
                curr = curr->next;
                }    
            }
            //start filling inner rectangle from next iterations onwards
            rowStart++; colStart++; rowEnd--; colEnd--;
        }
    return spiral;
    }
};
