/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    void generated(vector<vector<int>>& spiral, int m, int n, ListNode* head){
        int s=0, total = m*n, val;
        int top=0, right= n-1, down = m-1, left = 0;
        int tops=0, rights=1, downs=n-1, lefts=m-2;
        int tope=n-1, righte=m-2, downe=0, lefte=1;
        while(s<total){
            for(int i=tops; i <= tope; i++, ++s){
                if(head!=NULL){
                    val = head -> val;
                    head = head -> next;
                }
                else val = -1;
                spiral[top][i] = val;
            }
            if(s >= total) break;
            for(int i=rights; i <= righte; i++, ++s){
                if(head!=NULL){
                    val = head -> val;
                    head = head -> next;
                }
                else val = -1;
                spiral[i][right] = val;
            }
            if(s >= total) break;
            for(int i=downs; i >= downe; i--, ++s){
                if(head!=NULL){
                    val = head -> val;
                    head = head -> next;
                }
                else val = -1;
                spiral[down][i] = val;
            }
            if(s >= total) break;
            for(int i=lefts; i >= lefte; i--, ++s){
                if(head!=NULL){
                    val = head -> val;
                    head = head -> next;
                }
                else val = -1;
                spiral[i][left] = val;
            }
            if(s >= total) break;
            top++; right--; down--; left++;
            tops++; tope--; rights++; righte--; downs--; downe++; lefts--; lefte++;
        }
    }

    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        vector<vector<int>> spiral;
        for(int i=0; i < m; i++){
            vector<int> temp;
            for(int j=0; j < n; j++){
                temp.push_back(0);
            }
            spiral.push_back(temp);
        }
        generated(spiral, m, n, head);
        return spiral;
    }
};
```

