class Solution {
public:
    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        int minr = 0, maxr = m - 1, minc = 0, maxc = n - 1;

        vector<vector<int>> arr(m, vector<int>(n, -1));
        ListNode* temp = head;

        while (minc <= maxc && minr <= maxr) {
