            for (int i = minc; i <= maxc ; i++) {
                if (temp == NULL) return arr;
                arr[minr][i] = temp->val;
                temp = temp->next;
            }
            minr++;

            // Moving down
            for (int i = minr; i <= maxr; i++) {
                if (temp == NULL) return arr;
                arr[i][maxc] = temp->val;
                temp = temp->next;
            }
            maxc--;

            // Moving left
            if (minr <= maxr) { // Check added
                for (int i = maxc; i >= minc ; i--) {
                    if (temp == NULL) return arr;
                    arr[maxr][i] = temp->val;
                    temp = temp->next;
