                head=head->next;
            }
             j++;
             for(int a=j;a<m&&head;a++){
                 ans[a][n-1]=head->val;
                 head=head->next;
             }
            n--;
            for(int a=n-1;a>=i&&head;a--){
                ans[m-1][a]=head->val;
                head=head->next;
            }
            m--;
            for(int a=m-1;a>=j&&head;a--){
                ans[a][i]=head->val;
                head=head->next;
            }
            i++;
        }
        return ans;
    }
};
