# Code
```
class Solution {
    fun spiralMatrix(m: Int, n: Int, head: ListNode?): Array<IntArray> {
        val result = Array(m) { IntArray(n) }
        var lastRowCol = 0
        var cellCounter = 0
        var current = head

        while (cellCounter < m * n) {
            var row = lastRowCol
            var col = lastRowCol

            // Go right
            while (col < n - lastRowCol && cellCounter < m * n) {
                result[row][col] = current?.`val` ?: -1
                current = current?.next
                cellCounter++
                col++
            }

            col--
            row++

            // Go down
            while (row < m - lastRowCol && cellCounter < m * n) {
                result[row][col] = current?.`val` ?: -1
                current = current?.next
                cellCounter++
                row++
            }

            col--
            row--

            // Go left
            while (col >= lastRowCol && cellCounter < m * n) {
                result[row][col] = current?.`val` ?: -1
                current = current?.next
                cellCounter++
                col--
            }

            col++
            row--

            // Go up
            while (row > lastRowCol && cellCounter < m * n) {
                result[row][col] = current?.`val` ?: -1
                current = current?.next
                cellCounter++
                row--
            }

            lastRowCol++
        }

        return result
    }
}
