        while(j < n && target[j] == '_') j++;

		// if all chars are '_', break
        if(i == n || j == n) break;

        if(start[i] != target[j]) return false;

		//positional conditions
        if(start[i] == 'L') if(i < j) return false;
        if(start[i] == 'R') if(i > j) return false;
        i++; j++;
    }

    return i == n && j == n;
}
