    public boolean compute(String start, String target) 
    { 
        int LStart = 0, RStart = 0; 
        int LTarget = 0, RTarget = 0; 
        for (int i = 0; i < start.length(); i++) 
        { 
            char ch1 = start.charAt(i); 
            char ch2 = target.charAt(i); 
            if (ch1 == 'L') 
                LStart++; 
            else if(ch1 == 'R') 
                RStart++; 
            if (ch2 == 'L') 
                LTarget++; 
            else if(ch2 == 'R') 
                RTarget++; 
        } 

        if (LStart != LTarget || RStart != RTarget) 
            return false; 

        List<Integer> ar1 = new ArrayList<>(); 
        List<Integer> ar2 = new ArrayList<>(); 

        for (int i = 0; i < start.length(); i++) 
        { 
            char ch = start.charAt(i);
            if (ch != '_') { 
                ar1.add(i); 
