class Solution {
public:
    bool canChange(string start, string target) {
        int L=0,R=0;
        for(int i=0;i<start.size();i++)
        {
            if(start[i]=='R')
            {
                if(L!=0 || R<0) return false;
                R++;
            }

            if(target[i]=='R')
            {
                if(L!=0 || R<=0) return false;
                R--;
            }
            else if(target[i]=='L')
            {
                if(R!=0 || L<0) return false;
                L++;
            }

            if(start[i]=='L')
            {
                if(R!=0 || L<=0) return false;
                L--;
            }
        }
        return (R==0 && L==0);
