class Solution {
    public boolean canChange(String start, String target) {
        int i = 0;
        int j = 0;
        int n = start.length();

        while (i < n || j < n) {
            while (i < n && start.charAt(i) == '_') i++;
            while (j < n && target.charAt(j) == '_') j++;

            // should both reach the end to check the number of L and R
            // e.g. "_L" and "LL"
            if (i == n && j == n) return true;
            if (i == n || j == n) return false; 

            if (start.charAt(i) != target.charAt(j)) return false; // the same squence of R and L

            if (start.charAt(i) == 'L' && i < j) return false; // can not move L to the right
            if (start.charAt(i) == 'R' && i > j) return false; // can not move R to the left

            i++;
            j++;
        }

        return i == n && j == n;
    }
}
