public class Solution 
{
    public bool CanChange(string start, string target) 
    {
        int startR = 0, // free R's in start
            targetL = 0; // needed L's for target

        foreach ((int startC, int targetC) in start.Zip(target)) 
        {
            if (startC == 'R') ++startR; // we can move this R's to the right later
            if (targetC == 'L') ++targetL; // we need this L's, hope they will come to us later

            if (startC == 'L') 
            {
                if (targetL == 0 || startR != 0) return false; // target doesn't need L, or we didn't finished with R's yet
                --targetL; // target gets L
            }
            if (targetC == 'R')
            {
                if (startR == 0 || targetL != 0) return false; // we don't have free R or target want L's first
                --startR; // yeah we have that R for you
            } 
        }
        return targetL == 0 && startR == 0; // everyone is satisfied
    }
}
