public:
    bool canChange(string start, string target) {
        int pos1=0;
        int pos2=0;
        int n=start.length();
        int m=target.length();
        while(pos1<start.length() or pos2<target.length())
        {
            while(pos1<n and start[pos1]=='_')
            {
                pos1++;
            }
            while(pos2<m and target[pos2]=='_')
            {
                pos2++;
