class Solution {
public:
    bool canChange(string start, string target) {
        queue<pair<char,int>> left;
        queue<pair<char,int>> right;
        for(int i=0;i<start.length();i++) {
            if(start[i] != '_') left.push({start[i], i});
        }
        for(int i=0;i<target.length();i++) {
            if(target[i] != '_')right.push({target[i], i});
        }

        if(left.size() != right.size()) {
            return false;
        }

        while(!left.empty()) {
            pair<char,int> startQValue = left.front();
            pair<char,int> targetQValue = right.front();
            left.pop();
            right.pop();
            if(startQValue.first != targetQValue.first) {
                return false;
            }
            if(startQValue.first == 'L' && targetQValue.second > startQValue.second) {
                return false;
            } else if(startQValue.first == 'R' && targetQValue.second < startQValue.second) {
                return false;
            }
        }
        return true;
    }
};
