class Solution {
public:
    bool canChange(string s, string t) {
        int i = 0;          //For Target
        int j = 0;          //For start
        int n = s.size();   //Same for t

        while(i<n || j<n)
        {
            while(i<n && t[i] == '_') i++;      //Skip till you find a character
            while(j<n && s[j] == '_') j++;

            if(t[i] != s[j]) return false;

            if(t[i] == 'L' && j < i) return false;    //Since we know we can move L toward left only so
                                                      //If index of L in start is less than index of L in Target
            if(t[i] == 'R' && j > i) return false;    //Then it is impossible to to make target from start

            i++;                                      //Since we know we can move R toward Right only so
            j++;                                      //If index of R in start is greater than index of R in Target
        }                                             //Then it is impossible to to make target from start
        return true;
    }
};
