class Solution {
public:
    bool canChange(string start, string target) {

        // first we have to check if the numbers of 'R' and 'L' in both strings are equal or not.         
        int r1=0,l1=0;
        int r2=0,l2=0;

        for(int i=0;i<start.size();i++){
            if(start[i]=='R'){
                r1++;
            }
            else if(start[i]=='L'){
                l1++;
            }

            if(target[i]=='R'){
                r2++;
            }
            else if(target[i]=='L'){
                l2++;
            }
        }

        if(r1!=r2 || l1!=l2) return false;


        // here we shift only 'L', and we traverse from left to right and check if there is any 'R' before we hit
        // the required numbers of 'L' on the left side, then we return false... 

        // And if there is 'R' at ith position in start string and 'L' is at ith position in target string,then we also return false ...          

        int req = 0;

        for(int i=0;i<start.size();i++){

             if(start[i]=='R' && target[i]=='L'){
                 return false;
             }
             if(req>0 && start[i] == 'R'){
                   return false;
             }


             if(start[i] != 'L' && target[i]=='L'){
                  req++;
             }
             else if(req>0 && start[i]=='L'){
                  req--;
             }
        }


        if(req != 0) return  false;
        req = 0;

         // here we shift only 'R', and we traverse from right to left and check if there is any 'L' before we hit
        // the required numbers of 'R' on the right side, then we return false... 

        // And if there is 'L' at ith position in start string and 'R' is at ith position in target string,then we also return false ...          



        for(int i=start.size()-1;i>=0;i--){


             if(start[i]=='L' && target[i]=='R'){
                 return false;
             }

             if(req>0 && start[i] == 'L'){
                 return false;
             }

             if(start[i] != 'R' && target[i]=='R'){
                  req++;
             }
             else if(req>0 && start[i]=='R'){
                  req--;
             }

        }

        if(req != 0) return false;
        return true;
    }
};
```

