		for(int i=0;i<n;i++){
		if(s[i]==t[i]){
			if(s[i]=='L' && a>0){ // R ka count zyada hai so cannot go after L 
				ok = false;
				break;
			}
			if(s[i] == 'R' && c>0){ // L can not move right to R
				ok = false;
				break;
			}
		}
		if(s[i]=='R')a++; // R can only move to right, similiarly for L
		if(s[i]=='L')c--;
		if(t[i]=='R')a--; // c can only move to left t me jitn peeche aaye aage wale se counter ho jayenge 
		if(t[i]=='L')c++;
		if(a<0 || c<0 || (a>0 && c>0)){ // c peeche tb hi jayega jb raste me a nhi hoga 
			ok = false;
			break;
		}
	}
	if(a==0 && c==0 && ok) return true;
	return false;
	}
};
