    {
        int i=0;
        vector<char>mp;

        if(s==t)
            return 1;

        for(auto it:s)
        {
            if(it!='_')
                mp.emplace_back(it);
        }
        int x=mp.size();

        for(auto it:t)
        {
            if(it!='_')
            {
                if(!x || mp[i]!=it)
                    return 0;
                ++i;
            }
        }
        if(x-1==i)
            return 0;

        i=0;x=0;
        int n=t.size();

        while(i<n && x<n)
        {
            while(i<n && s[i]=='_')
                ++i;
            while(x<n && t[x]=='_')
                ++x;

            if(s[i]=='L' && i>=x)
                ++i,++x;

            else if(s[i]=='R' && i<=x)
                ++i,++x;
            else
            {
                if(i<n||x<n)
                    return 0;
            }
        }

        return 1;    
    }
};

