class Solution {
    public boolean canChange(String start, String target) {
        int index = 0;
        for (int i = 0; i < target.length(); i++) {
            if (target.charAt(i) == '_')
                continue;

            // get the character index from start
            while (index < start.length() && start.charAt(index) != target.charAt(i)) {
                if (start.charAt(index) != '_')
                    return false;
                index++;
            }

            // check if valid
            if (index == start.length()
                    || (target.charAt(i) == 'L' && index < i)
                    || (target.charAt(i) == 'R' && index > i))
                return false;

            index++;
        }

        for (int i = index; i < start.length(); i++)
            if (start.charAt(i) != '_')
                return false;

        return true;
    }
}
