class Solution {
    public boolean canChange(String start, String target) {
        Stack<int[]> st = new Stack();
        // arr[0] - > index
		// arr[1] - > 1 for 'R' and  0 for 'L'

        for(int i = 0; i < target.length(); i++) {
            int idx = i+1;          // for easy to understanding (1 base indexing)
            if(target.charAt(i) == 'L') {
                st.push(new int[]{idx, 0});
            } 

            if(target.charAt(i) == 'R') {
                st.push(new int[]{idx, 1});
            } 
        }

		// checking from last cause last temp[] at the top of stack

        for(int i = start.length()-1; i >= 0; i--) {
            int idx = i+1;

            if(start.charAt(i) == 'L') {
                if(st.size() > 0) {
                    int[] peek = st.peek();
                    if(peek[1] == 0 && peek[0] <= idx) {     // first check -> peek is 'L' or not    second check-> it appers before or at idx 
                        st.pop();
                    } else {
                        return false;
                    }
                } else {
                    return false;    // we need 'L' but stack is empty so return false
                }
            } 

            if(start.charAt(i) == 'R') {
                if(st.size() > 0) {
                    int[] peek = st.peek();
                    if(peek[1] == 1 && peek[0] >= idx) {   // first check -> peek is 'R' or not    second check-> it appers after or at idx 
                        st.pop();
                    } else {
                        return false;    
                    }
                } else {
                    return false;     // we need R' but stack is empty so return false
                }
            } 
        }
        return st.size() == 0;
    }
}
