```c++
class Solution {
public:
    bool canChange(string start, string target) {
        int u_in_s=count(begin(start),end(start),'_');
        int u_in_t=count(begin(target),end(target),'_');
        if(u_in_s!=u_in_t) return false;

        int i=0, j=0;
        while(i<start.size() && j<target.size()) {
            if(start[i]=='_') {
                i++;
                continue;
            }
            if(target[j]=='_') {
                j++;
                continue;
            }
            if(start[i]!=target[j]) return false;    //both i and j should point to the same character
            if(start[i]=='L' and i<j) return false; //since L can only move left
            if(start[i]=='R' and i>j) return false; //since R can only move right
            i++; j++;
        }

        return true;
    }
};
