public boolean canChange(String s, String t) {
        //the length should be the same and the letters should follow the same order
        if (s.length()!=t.length()) return false; 
        if (!s.replace("_","").equals(t.replace("_",""))) return false; 
        //going right to left there shouldn't be more 'R' s in starting string  than in the target string.
        for (int i=t.length()-1, rCount=0;i>=0;i--){ 
            rCount+=t.charAt(i)=='R'?1:0;
            rCount-=s.charAt(i)=='R'?1:0;
            if (rCount<0) return false;
        }
        //going left to right there shouldn't be more 'L' s in starting string  than in the target string.
        for (int i=0,lCount=0;i<t.length();i++){ 
            lCount+=t.charAt(i)=='L'?1:0;
            lCount-=s.charAt(i)=='L'?1:0;
            if (lCount<0) return false;
        }
        return true;
    }
