class Solution {
public:
    bool canChange(string s1, string s2) {
        stack<pair<char, int>> st1, st2;
        for(int i=0; i<s1.size(); i++)
        {
            if(s1[i]=='L')
            {
                st1.push({'L', i});
            }
            else if(s1[i]=='R')
            {
                st1.push({'R', i});
            }
            if(s2[i]=='L')
            {
                st2.push({'L', i});
            }
            else if(s2[i]=='R')
            {
                st2.push({'R', i});
            }
        }

        if(st1.size() != st2.size())
        {
            return false;
        }
        while(!st1.empty() and !st2.empty())
        {
            if(st1.top().first != st2.top().first)
            {
                return false;
            }

            if(st1.top().first == 'R')
            {
                if(st1.top().second > st2.top().second)
                {
                    return false;
                }
            }
            else
            {
                if(st1.top().second < st2.top().second)
                {
                    return false;
                }
            }
            st1.pop();
            st2.pop();
        }
        return true;
    }
};
```

