class Solution:
    def canChange(self, start: str, target: str) -> bool:
        # make two pointer to traverse start and target (i,j)
        i = j = 0
        n = len(start)

        while i < n or j < n:
            # use to skip "_" from start
            while i < n and start[i] == '_':
                i += 1
            # use to skip "_" from target
            while j < n and target[j] == '_':
                j += 1
            # i == j == n means reached end of start or target
            if n in (i,j):
                return i == j == n

            if start[i] != target[j]:
                return False

            if start[i] == 'L':
                if i < j:
                    return False
            else:
                if i > j:
                    return False
            i += 1
            j += 1
        return True
